const express = require("express");
const router = express.Router();
const { createRecord, markAttendance, syncAttendance } = require("../controllers/attendance.controller");
const authenticate = require("../middlewares/auth.middleware");

router.post("/create", authenticate, createRecord);
router.post("/mark", authenticate, markAttendance);
router.post("/sync", authenticate, syncAttendance);

module.exports = router;
